    private void jTxtKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTxtKeyTyped
        JTextField jTxt = (JTextField) evt.getComponent();

        if (jTxt.getSelectedText() != null && jTxt.getSelectedText().length()
                != jTxt.getText().length()) {
            evt.consume();
        } else if (jTxt.getSelectedText() == null && jTxt.getText().length() >= 2) {
            evt.consume();
        }
    }//GEN-LAST:event_jTxtKeyTyped

    private void minTxtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_minTxtKeyReleased
        switch (evt.getKeyCode()) {
            case java.awt.event.KeyEvent.VK_UP:
            case java.awt.event.KeyEvent.VK_DOWN:
            case java.awt.event.KeyEvent.VK_LEFT:
            case java.awt.event.KeyEvent.VK_RIGHT:
                return;
            default: {
                if (minTxt.getSelectedText() == null && Global.isNumber(minTxt.getText())
                        && (minTxt.getText().length() == 2
                        || evt.getKeyCode() == java.awt.event.KeyEvent.VK_ENTER)) {
                    secTxt.requestFocus();
                }
            }
        }
    }//GEN-LAST:event_minTxtKeyReleased

    private void secTxtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_secTxtKeyReleased
        switch (evt.getKeyCode()) {
            case java.awt.event.KeyEvent.VK_UP:
            case java.awt.event.KeyEvent.VK_DOWN:
            case java.awt.event.KeyEvent.VK_LEFT:
            case java.awt.event.KeyEvent.VK_RIGHT:
                return;
            default: {
                if (secTxt.getSelectedText() == null && Global.isNumber(secTxt.getText())
                        && (secTxt.getText().length() == 2
                        || evt.getKeyCode() == java.awt.event.KeyEvent.VK_ENTER)) {
                    milTxt.requestFocus();
                }
            }
        }
    }//GEN-LAST:event_secTxtKeyReleased

    private void milTxtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_milTxtKeyReleased
        switch (evt.getKeyCode()) {
            case java.awt.event.KeyEvent.VK_UP:
            case java.awt.event.KeyEvent.VK_DOWN:
            case java.awt.event.KeyEvent.VK_LEFT:
            case java.awt.event.KeyEvent.VK_RIGHT:
                return;
            default: {
                if (milTxt.getSelectedText() == null && Global.isNumber(milTxt.getText())
                        && (milTxt.getText().length() == 2
                        || evt.getKeyCode() == java.awt.event.KeyEvent.VK_ENTER)) {
                    okBtn.requestFocus();
                }
            }
        }
    }//GEN-LAST:event_milTxtKeyReleased

    private void jTxtFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTxtFocusLost
        JTextField jTxt = (JTextField) evt.getComponent();

        if (!Global.isNumber(jTxt.getText())) {
            jTxt.setText("00");
        } else {
            jTxt.setText(Time.normalizeFactor(jTxt.getText()));
        }
    }//GEN-LAST:event_jTxtFocusLost

    private void jTxtFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTxtFocusGained
        JTextField jTxt = (JTextField) evt.getComponent();
        jTxt.selectAll();
    }//GEN-LAST:event_jTxtFocusGained

    private void okBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_okBtnActionPerformed
        time.setMinutes(minTxt.getText());
        time.setSeconds(secTxt.getText());
        time.setMiliseconds(milTxt.getText());
        this.dispose();
    }//GEN-LAST:event_okBtnActionPerformed

    private void formWindowClosing(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowClosing
        time.setId(Global.NO_INDEX);
    }//GEN-LAST:event_formWindowClosing

    private void jTxtFocusLostNormalize(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTxtFocusLostNormalize
        if (!secTxt.getText().isBlank() && Global.isNumber(secTxt.getText())) {
            int min = Integer.parseInt(minTxt.getText());
            int sec = Integer.parseInt(secTxt.getText());

            if (sec > 60) {
                while (sec > 60) {
                    min = min != 99 ? min + 1 : min;
                    sec -= 60;

                }

                secTxt.setText(Time.normalizeFactor(String.valueOf(sec)));
                minTxt.setText(Time.normalizeFactor(String.valueOf(min)));
            }
        }
    }//GEN-LAST:event_jTxtFocusLostNormalize

